public void cadastrar(){
        Banco banco = new Banco();
        
        try{
            if("".equals(campoNome.getText())){
                throw new CampoVazioException();
            } else {
                Artesao a = new Artesao();
                a.setNome(campoNome.getText());
                a.setFerreiro_nome(campoNomeFerreiro.getText());
                
                banco.inserirArtesao(a);
                
                JOptionPane.showMessageDialog(null, "Artesao cadastrado com sucesso!");
                }
        }catch (CampoVazioException e){
            JOptionPane.showMessageDialog(null, "Preencha os campos corretamente!");
        }
        
    }
